!(function (i) {
  i("html").removeClass("no-js"),
    i("header a").click(function (e) {
      var t;
      i(this).hasClass("no-scroll") ||
        (e.preventDefault(),
        (t = i(this).attr("href")),
        (e = i(t).offset().top),
        i("html, body").animate(
          { scrollTop: e + "px" },
          +Math.abs(window.pageYOffset - i(t).offset().top)
        ),
        i("header").hasClass("active") &&
          i("header, body").removeClass("active"));
    }),
    i("#to-top").click(function () {
      i("html, body").animate({ scrollTop: 0 }, 500);
    }),
    i("#lead-down span").click(function () {
      var e = i("#lead").next().offset().top;
      i("html, body").animate({ scrollTop: e + "px" }, 500);
    }),
    i("#experience-timeline").each(function () {
      ($this = i(this)),
        ($userContent = $this.children("div")),
        $userContent.each(function () {
          i(this)
            .addClass("vtimeline-content")
            .wrap(
              '<div class="vtimeline-point"><div class="vtimeline-block"></div></div>'
            );
        }),
        $this.find(".vtimeline-point").each(function () {
          i(this).prepend(
            '<div class="vtimeline-icon"><i class="fa fa-map-marker"></i></div>'
          );
        }),
        $this.find(".vtimeline-content").each(function () {
          var e = i(this).data("date");
          e &&
            i(this)
              .parent()
              .prepend('<span class="vtimeline-date">' + e + "</span>");
          e = i(this).data("location");
          e &&
            i(this)
              .parent()
              .prepend('<span class="vtimeline-location">' + e + "</span>");
        });
    }),
    i("#mobile-menu-open").click(function () {
      i("header, body").addClass("active");
    }),
    i("#mobile-menu-close").click(function () {
      i("header, body").removeClass("active");
    }),
    i("#view-more-projects").click(function (e) {
      e.preventDefault(),
        i(this).fadeOut(300, function () {
          i("#more-projects").fadeIn(300);
        });
    });
})(jQuery);
